import * as React from "react";
import {Form} from 'react-lifesg-design-system'

interface Props {
  name: string;
  label: string;
  placeholder?: string;
  selectedValue: string;
  // onChange: (fieldName: string, value: string) => void;
  error?: string;
  // options: string[][]; 
}

export const Dropdown: React.FunctionComponent<Props> = (props) => {
  return (
    <div className={formatWrapperClass(props)}>
      <label htmlFor={props.name}>{props.label}</label>
      <div className="field">
        <Form.Select 
          name={props.name}
          className="form-control"
          placeholder={props.placeholder}
          options={[
            { value: "A31231", label: "Option A" },
            { value: "B1231231", label: "Option B" },
            { value: "C1231231", label: "Option C" },
            { value: "12", label: "Option D" },
          ]}
          // label={props.value}
          listExtractor={(item) => item.label}
          displayValueExtractor={(item) => item.label}
          valueExtractor={({selectedValue}) =>   item.value}

          // selectedOption = {props.selectedValue}
          // valueExtractor={(item) => {props.selectedValue = item.value }}

          // onSelectItem={(item, extractedValue) => someFn()}

          // valueExtractor={(props) => props.value}
          // onSelectItem={(item, extractedValue) => {extractedValue = props.value  } }
          // onselect ={ props.value}
          // valueExtractor={(item) => props.value}
          // onChange={onChangeInput(props)}
          // onSelectItem={
          //   console.log("st")
          // }
        />
      </div>
      <div className="help-block">{props.error}</div>
    </div>
  )
};

const formatWrapperClass = (props: Props) => {
  const wrapperClass = 'form-group';

  return props.error ?
    `${wrapperClass} has-error` :
    wrapperClass;
};

