import * as React from "react";
import {Form} from 'react-lifesg-design-system'
import { options } from "toastr";

interface Props {
  name: string;
  label: string;
  placeholder?: string;
  selectedItemValaue: any;
  onChange: (fieldName: string, value: any) => void;
  error?: string;
  // options: string[][]; 
}

export const Dropdown: React.FunctionComponent<Props> = (props) => {
  return (
    <div className={formatWrapperClass(props)}>
      <label htmlFor={props.name}>{props.label}</label>
      <div className="field">
        <Form.Select 
          name={props.name}
          className="form-control"
          placeholder={props.placeholder}
          options={[
            { value: "A", label: "Option A" },
            { value: "B", label: "Option B" },
            { value: "C", label: "Option C" },
            { value: "D", label: "Option D" },
          ]}

          // selectedOption = {props.selectedItemValaue}

          // selectedOption = {(item) => console.log(item)}

          // label={props.value}
          // listExtractor={(item) => item.label}
          valueExtractor={(item) => item.value}
          listExtractor={(item) => item.label}
          displayValueExtractor={(item) => item.label}
          // onChange={

          //   onChangeInput(props)



          // }

          // onSelectItem = {(item, extractedValue) => {
          //   extractedValue = {props.selectedItemValaue}
          // } }

          // onSelectItem={(item) => console.log(
          //    item.value = props.selectedValue
          // )}
          // selectedOption = {props.selectedValue}
          // valueExtractor={(item) => {props.selectedValue = item.value }}

          // onSelectItem={(item, extractedValue) => someFn()}

          // valueExtractor={(props) => props.value}
          // onSelectItem={(item, extractedValue) => {extractedValue = props.value  } }
          // onselect ={ props.value}
          // valueExtractor={(item) => props.value}
          // onChange={onChangeInput(props)}
          // onSelectItem={
          //   console.log("st")
          // }
        />
      </div>
      <div className="help-block">{props.error}</div>
    </div>
  )
};

const formatWrapperClass = (props: Props) => {
  const wrapperClass = 'form-group';

  return props.error ?
    `${wrapperClass} has-error` :
    wrapperClass;
};

const onChangeInput = (props: Props) => (e: React.ChangeEvent<HTMLSelectElement>) => { 
  props.onChange(e.target.name, e.target.value);

};
