import React from "react";
import { Link } from "react-router-dom";
import Booking from "../models/booking";
import BaseService from "../service/base.service";
import { Text, Button, Layout, BoxContainer, Breadcrumb, InputGroup, Accordion, InputSelect, Modal, RadioButton, } from 'react-lifesg-design-system';

import * as toastr from "toastr";
import styled from "styled-components";
import { render } from "react-dom";


const StyledContainer = styled(Layout.GridContainer)`
    grid-template-rows: 1fr;
    grid-template-columns: 12fr;
    grid-template-areas:
        "title"
        "button "
        "queue";
    grid-gap: 0.25rem;
    grid-row-gap: 16px;
`;

const StyledSection = styled(Layout.Section)`
    margin-top: 8%;
    min-height: 600px;
`;

const Main = styled.main`
  color: white;
  grid-area: queue;
  padding: 0.25rem;
`;

function Del(Id?: string) {
  BaseService.delete("/booking/del/", {
    id: Id,
  }).then((rp) => {
    if (rp.Status) {
      toastr.success("Member saved.");
      window.location.reload();
    } else {
      toastr.error(rp.Messages);
      console.log("Messages: " + rp.Messages);
      console.log("Exception: " + rp.Exception);
    }
  });
}

