import React from 'react';

import { Text, Form, Button, Layout, Banner, LinkList } from 'react-lifesg-design-system';
import { ThemeProvider } from "styled-components";
import styled from "styled-components";

interface Props {
    onChange: (fieldName: string, value: string) => void;
    onSave: () => void;
}

const StyledContainer = styled(Layout.GridContainer)`
    grid-template-rows: 1fr 1fr 1fr 4fr;
    grid-template-columns: 12fr;
    grid-template-areas:
        "title title title title"
        "buttons buttons buttons buttons"
        "queue queue queue queue";
    grid-gap: 0.25rem;
    padding: 25px;

`;

const StyledSection = styled(Layout.Section)`
    margin-top: 8%;
    min-height: 600px;
`;


const Title = styled.div`
  background: #3a3a55;
  grid-area: title;
  padding: 0.25rem;
`;
const Main = styled.main`
  background: #1f2128;
  color: white;
  grid-area: buttons;
  padding: 0.25rem;
`;
const SideBar = styled.div`
  /* background: #9aaab7; */
  grid-area: queue;
  padding: 0.25rem;
`;

const dashboard: React.FunctionComponent<Props> = (props) => {
  return (
      <>
        <StyledSection>
            <StyledContainer>
                <Title><Text.H3>Currently Serving</Text.H3></Title>
                <Main>
                    <Button.Default
                        onClick={props.onSave}
                    >Recall</Button.Default>
                    <Button.Default
                        onClick={props.onSave}
                    >Next Number</Button.Default>
                </Main>
                <SideBar>
                
                    <LinkList items={[{
                title: "First item",
                href: "https://www.google.com",
                target: "_blank"
            }, {
                title: "Second item",
                description: "Excepteur sint occaecat cupidatat non proident",
                href: "https://www.google.com",
                target: "_blank"
            }, {
                title: "Third item",
                description: "Lorem ipsum dolar sit amet",
                href: "https://www.google.com",
                target: "_blank"
            }, {
                title: "Fourth item",
                description: "Ut enim ad minima veniam, quis nostrum exercitationem",
                href: "https://www.google.com",
                target: "_blank"
            }]} style="small" maxShown={2} />
             
                </SideBar>
                    
                
            </StyledContainer>
        </StyledSection>
    </>
  )
}

export default dashboard;
