import React from 'react';
import '../App.css';
import { 
    Text, 
    Form, 
    Button, 
    Layout, 
    Banner, 
    LinkList,
    Breadcrumb,
    BoxContainer,
    InputSelect 
} from 'react-lifesg-design-system';
import { ThemeProvider } from "styled-components";
import styled from "styled-components";

interface Props {
    onChange: (fieldName: string, value: string) => void;
    onSave: () => void;
}

// const StyledContainer = styled(Layout.GridContainer)`
//     grid-template-rows: 1fr 1fr 4fr 0fr;
//     grid-template-columns: 12fr;
//     grid-template-areas:
//         "title title title title"
//         "button button button button"
//         "queue queue queue queue";
//     grid-gap: 0.25rem;
//     padding: 25px;

// `;

const StyledSection = styled(Layout.Section)`
    margin-top: 8%;
    min-height: 600px;
`;

const Accordion = styled(Text.Body)`
    min-height: 300px;
`;


const Title = styled.div`
  grid-area: title;
  padding: 0.25rem;
`;
const NavBar = styled.div`
  grid-area: button;
  padding: 0.25rem;
`;
const Main = styled.main`
  color: white;
  grid-area: queue;
  padding: 0.25rem;
`;


const ServiceCounter: React.FunctionComponent<Props> = (props) => {
  return (
      <>
        <StyledSection>
        <Layout.Container>
        <Breadcrumb links={[ { title: 'Home' , url: 'http://localhost:3000/ServiceCounter' }, { title: 'Service Counter' } ]} />
            
                <Layout.GridContainer className="column2">
                <div>
                    <Text.H3>Currently Serving</Text.H3>
                    <div className='spacer1'></div>
                    <Text.Body>Government services appointments</Text.Body>
                </div>
                {/* <div className="inlinecontent" style={{justifyItems: "end", alignItems: "center"}}>
                    <Button.Default
                        onClick={props.onSave}
                    >Next Number</Button.Default>
                </div> */}
                </Layout.GridContainer>
                {/* <Main>
                    <LinkList items={[{
                    title: "First queue",
                    href: "https://www.google.com",
                    target: "_blank"
                }, {
                    title: "Second queue",
                    description: "Excepteur sint occaecat cupidatat non proident",
                    href: "https://www.google.com",
                    target: "_blank"
                }, {
                    title: "Third queue",
                    description: "Lorem ipsum dolar sit amet",
                    href: "https://www.google.com",
                    target: "_blank"
                }, {
                    title: "Fourth queue",
                    description: "Ut enim ad minima veniam, quis nostrum exercitationem",
                    href: "https://www.google.com",
                    target: "_blank"
                }]} style="small" maxShown={2} />
                </Main>
              */}
                {/* <BoxContainer title="This is the title">
                    <div style={{
                        padding: "2rem"
                    }}>
                        <Accordion>
                        <InputSelect
                            placeholder="Select"
                            options={[
                                { value: "A", label: "Option A" },
                                { value: "B", label: "Option B" },
                                { value: "C", label: "Option C" },
                                { value: "D", label: "Option D" },
                            ]}
                            valueExtractor={(item) => item.value}
                            listExtractor={(item) => item.label}
                            displayValueExtractor={(item) => item.label}
                        />
                        <div style={{marginTop: "50px"}}>
                            <Button.Small styleType="secondary">Transfer</Button.Small>
                        </div>
                        </Accordion>
                    </div>
                </BoxContainer> */}

            <Layout.GridContainer>
                    <Text.H3>Appointments</Text.H3>
                    <Text.Body>Government services appointments</Text.Body>
                    <Main>
                    <Accordion.Base className='base' >
                        {this.state.bookings.map(function (input, index) {
                            return (
                                <Accordion.Item title={input.CitizenName} key={index} expanded={false}>
                                    <Text.Body>
                                        <ul>
                                            <li>
                                                <b>Citizen Name :</b>  {input.CitizenName}
                                            </li>
                                            <li>
                                                <b>Citizen Number: </b> {input.CitizenNumber}
                                            </li>
                                            <li>
                                                <b>Citizen Email: </b> {input.CitizenEmail}
                                            </li>
                                            <li>
                                                <b>Service start date:</b>  {input.ServiceStartDate}
                                            </li>
                                            <li>
                                                <b>Service start time:</b>  {input.ServiceStartTime}
                                            </li>
                                            <li>
                                                <b>Service Name:</b>  {input.ServiceName}
                                            </li>
                                            <li>
                                                <b>Service Provider Location:</b>  {input.ServiceProviderLocation}
                                            </li>
                                        </ul>
                                    </Text.Body>
                                </Accordion.Item>
                            )
                        })}
                    </Accordion.Base>
                </Main>

                <div className="spacer3"></div>

            </Layout.GridContainer>

        </Layout.Container>
        </StyledSection>
    </>
  )
}

export default ServiceCounter;
